function MentorMatching() {
  const [skill, setSkill] = useState('');
  const [language, setLanguage] = useState('');
  const [matchedMentor, setMatchedMentor] = useState(null);
  const [viewLang, setViewLang] = useState('en');
  const [message, setMessage] = useState('');
  const [showDetails, setShowDetails] = useState(false);

  const handleFind = () => {
    setMatchedMentor(null);
    setMessage('');
    setShowDetails(false);

    if (!skill.trim() || !language.trim()) {
      setMessage('â— Please enter both skill and language.');
      return;
    }

    const lang = language.toLowerCase();
    setViewLang(lang === 'hindi' ? 'hi' : 'en');

    const found = mentors.find(m =>
      m.skill.toLowerCase() === skill.toLowerCase() &&
      m.language.toLowerCase() === lang
    );

    if (found) {
      setMatchedMentor(found);
    } else {
      setMessage('ğŸš« No mentor match found.');
    }
  };

  return (
    <div className="mentor-section">
      <h2>Find a Mentor</h2>
      <div className="input-row">
        <input placeholder="Enter Skill (e.g., Tailoring)" value={skill} onChange={e => setSkill(e.target.value)} />
        <input placeholder="Enter Language (e.g., Hindi)" value={language} onChange={e => setLanguage(e.target.value)} />
        <button className="find-btn" onClick={handleFind}>Find</button>
      </div>

      {message && <p className="message">{message}</p>}

      {matchedMentor && (
        <div className="mentor-card fade-in">
          <img src={matchedMentor.photo} alt={matchedMentor.name} className="mentor-photo" />
          <h3>{viewLang === 'en' ? matchedMentor.name : matchedMentor.name_hi}</h3>
          <p>{viewLang === 'en' ? matchedMentor.bio_en : matchedMentor.bio_hi}</p>
          <div className="lang-buttons">
            <button onClick={() => setViewLang('en')}>View in English</button>
            <button onClick={() => setViewLang('hi')}>View in Hindi</button>
          </div>
          <button className="detail-btn" onClick={() => setShowDetails(true)}>Show Full Details</button>
        </div>
      )}

      {showDetails && matchedMentor && (
        <div className="mentor-detail-card fade-in">
          <h3>{viewLang === 'en' ? matchedMentor.name : matchedMentor.name_hi}</h3>
          <p><strong>Skill:</strong> {matchedMentor.skill}</p>
          <p><strong>Language:</strong> {matchedMentor.language}</p>
          <p><strong>Bio:</strong> {viewLang === 'en' ? matchedMentor.bio_en : matchedMentor.bio_hi}</p>
          <button onClick={() => setShowDetails(false)}>Close</button>
        </div>
      )}
      <Link to="/mentors">
  <button className="see-all-btn">ğŸ‘©â€ğŸ« See All Mentors</button>
</Link>
    </div>
    
  );
}

export default MentorMatching;
